package com.example.peanuts;import java.util.List;public class GroupItem {    private List<NewAccount.User> members;    private List<String> restrictions;    private List<FoodItem> foods;    private String groupName;    public GroupItem(String name, List<NewAccount.User> members,                     List<String> restrictions, List<FoodItem> foods) {        this.groupName = name;        this.members = members;        this.restrictions = restrictions;        this.foods = foods;    }    public String getGroupName() { return groupName;}    public List<NewAccount.User> getMembers() { return members; }    public List<String> getRestrictions() { return  restrictions; }    public List<FoodItem> getFoods() { return foods; }}